import {Component} from 'react'
import { Link,Route} from 'react-router-dom'

import Home from './Components/Home'
import Youxitese from './Components/Youxitese'
import Shequapp from './Components/Shequapp'

export default class App extends Component {
    // render() {
    //     return (

    //         <div>
    //             <Welcome />
    //         </div>
    //     )
    // }

    dengluzhuce =() => { 
        console.log('登录注册');
     }

    render() {
        return (
            <div>
                <div className="row">
                    <div className="col-xs-offset-2 col-xs-8">
                        <div className="page-header"><h2>React Router Demo</h2></div>
                    </div>
                </div>
                <div className="row">
                    <div className="col-xs-2 col-xs-offset-2">
                        <div className="list-group">

                            {/* 
                                如需使用route路由的话，需要在index.jsx里面引入“import { Link, BrowserRouter } from 'react-router-dom'” 并包裹App
                                当然，也可以在这里包，但是在App里面包的话，范围更大，一劳永逸。
                            */}
                                <Link className="list-group-item" to="/home" >官网首页</Link>
                                <Link className="list-group-item" to="/youxitese" >游戏特色</Link>
                                <Link className="list-group-item" to="/shequapp" >社区APP</Link>
                                <button className='loginBtn' onClick={this.dengluzhuce}>登录/注册</button>
                        </div>
                    </div>
                    <div className="col-xs-6">
                        <div className="panel">
                           
                            <Route path="/home" component={Home}/>
                            <Route path="/youxitese" component={Youxitese}/>
                            <Route path="/shequapp" component={Shequapp}/>
                            
                        </div>
                    </div>
                </div>
            </div>
        )
    }

}
